	newkdef[50]=function()
	--重写天宁寺功德箱事件
	--OEVENTLUA[50] = function()
		local title = "功德箱";
		local str = "旁边有个功德箱，想要做些什么呢？";
		local btn = {"捐赠","慈悲","偷窃","抢劫", "路过"};
		local num = #btn;
		local r = LGMsgBox(title,str,btn,num,0,1);
		
		--捐赠，道德最大是60
		if r == 1 then
			instruct_1(236,0,1);			--银子太多了也没啥用，拿出*100两做慈善事业吧。
			instruct_0();
			if instruct_31(100) then			--有没有超过100两
				instruct_2(174,-100);		--物品[银两]+[-100]
				if JY.Person[0]["品德"] < 65 then
					instruct_37(1)
				end
				say("愿佛祖保佑我早日脱单，阿弥托佛。",0,1) 
				Cls()
			end
		elseif r ==  2 then		--慈悲：道德为最大65
			say("江陵城的人不会骗人吧？",0,1);
			local gold = 0;
			for i=JY.Person[0]["品德"]+1, 100 do
				if CC.Gold - gold >= 100 then			--有没有超过100两
					gold = gold + 100;
					JY.Person[0]["品德"] = i;
					if JY.Person[0]["品德"] > 65 then 
						JY.Person[0]["品德"] = 65
					end
				end
			end
			if instruct_31(10000) and instruct_16(738) == false then 
				instruct_2(174,-2000);
				say("佛祖，慧妹已经离开我一年多了，我全部家当全捐给你了，我要会安家过日子的。",0,1)
				if instruct_18(306) then
					dark()
					addevent(63,23,1,nil,nil,4841*2)
					instruct_10(738)
					JY.Person[738]["姓名"] = "郎剑秋"         
					light()
					say("我...我不是做梦吧。胡夫人，怎么是你?你的伤好了吗？",0,1)
					Cls()
					say("大兄弟，多谢你救了我。你心地太善，江湖险恶，让剑秋陪你走一程。我本名郎剑秋，以后你就叫我剑秋好了。等哪天你不需要我了，我再回玉笔山庄。",738,0)
					Cls()
					say("那真是太好了。Ｄ＜不需要她了？嗯，看来不能轻易让她离开我身边。＞",0,1)
					Cls()
					dark()
					null(63,23)
					light()
					Cls()
					if instruct_16(1) then
						say("这就是我妈妈？看来"..JY.Person[0]["姓名"].."也不是好人。",1,1)
						null(70,5)
						instruct_21(1)
					end	
				end 	
			else
				say("还得再努力，等我有钱了再来烧香还愿。",0,1)	
				Cls()
			end	
		elseif r == 3 then		--偷窃：减少5点道德得100两
			if instruct_28(0, 5, 999, 6, 0) == false then
				instruct_1(235, 0, 1)
				return 
				instruct_0()
			end
			instruct_1(234, 0, 1)
			instruct_0()
			if instruct_11(0, 8) == true then
				instruct_0()
				if JY.Base["标准"] ~= 7 then		--仁者没有钱得
					instruct_2(174, 100)
				end
				instruct_0()
				instruct_37(-5)
				return 
			end			
		elseif r == 4 then			--抢劫：根据道德判断，每次减5点
			if instruct_28(0, 5, 999, 6, 0) == false then
				instruct_1(235, 0, 1)
				return 
				instruct_0()
			end
			say("这谁的银子！没人应我拿了啊。",0,1);
			local gold = 0;
			for i=JY.Person[0]["品德"]-5, 0, -5 do
				if JY.Base["标准"] ~= 7 then		--仁者没有钱得
				gold = gold + 100;
			end
			JY.Person[0]["品德"] = i;
			end
			if gold > 0 then
				say("大胆，连香火钱也敢偷，拿下。",70,0,"主持");
				Cls()
				say("...",0,1)
				Cls()			
				if WarMain(382) == false then
					instruct_15()
					Cls()
				end
				say("好险，还好小爷我轻功好。",0,1);
				instruct_2(174, gold);

			end
		end
	end



