newkdef[600]=function()
	instruct_27(3,6342,6348)  --显示人物动画
	Cls()  --清屏
	TalkEx("逍遥派不肖弟子苏星河，拜见本派新任掌门。", 115, 0)  --对话
	Cls()  --清屏
	TalkEx("老前辈行此大礼，可折杀小僧了。", 49, 1)  --对话
	Cls()  --清屏
	TalkEx("师弟，你是我师父的关门弟子，又是本派掌门，我虽是师兄，却也要向你磕头！", 115, 0)  --对话
	Cls()  --清屏
	TalkEx("这个……这个……", 49, 1)  --对话
	Cls()  --清屏
	TalkEx("师弟，你真是福泽深厚。我和丁春秋想得师父传授逍遥派绝学，想了几十年，始终不入师父法眼，你却在一个时辰之内，便受到师父的垂青。", 115, 0)  --对话
