newkdef[605]=function()
	local sid = 261
	for i = 1,CC.TeamNum do 
		if JY.Base['队伍'..i] >= 0 then 
			local id = JY.Base['队伍'..i]
			if JY.Person[0]['门派'] == 28 then
				if  JY.Person[id]['畅想分阶'] < 4  then
					say('要参悟本门至高绝学吗?',sid,1) 
					if DrawStrBoxYesNo(-1, -1, "挑战准备好了吗？", C_WHITE, CC.DefaultFont) then 
						if WarMain(110) ==  false then 
							instruct_15()
							Cls()
							do  return end 
						end
						instruct_33(id,170,50)
						instruct_33(id,415,50)
						Cls()
						add(-2,6,1,606,1,nil)
						do  return end 
					end
				else
					say('本门绝学没有秘籍可以修炼，需要在日常武学修炼中自动参悟。',sid,1)
					Cls()
				end
			else
				say('本门绝学没有秘籍可以修炼，需要在日常武学修炼中自动参悟。',sid,1)
				Cls()
			end 
		end
	end
	Cls()  --清屏
	do return end
end

