					end
				elseif r == 3 then
					say(JY.Person[id]["姓名"].."，修炼武学，需勤加练习，你找为师有何事？",tid,0)
				elseif r == 4 then
					local r1 = LGMsgBox("任务", "行走江湖，首重一个侠字，你意欲何为？  ", {"龙门镖局"," 声谷踪迹 "," 围攻光明顶 ","屠师大会"}, 4, tid)
