						if CC.TX["门派任务"] < 1 then	
							TalkEx("你六师叔身受重伤，五师叔去Ｄ＜杭州龙门镖局＞Ｗ也有多日了，你代宋某去龙门镖跑一趟？带个口信给我你五师弟，望早日归来，不可轻易涉险。", tid, 0)  --对话
							Cls()  --清屏
							TalkEx("弟子定寻到五师叔把口信带到。", id, 1)  --对话
							Cls()  --清屏
							addevent(133,31,0,1164,3,nil)									
						end
					elseif r1 == 2 then
						if CC.TX["门派任务"] < 2 then
							say(JY.Person[id]["姓名"].."，你七师叔出门有一段日子了，我心中甚是挂念，你下山去代我寻找他的行踪。",tid,0)	
							Cls()
							say("是！师傅。",0,1)
