						if CC.TX["门派任务"] < 3 then
							say(JY.Person[id]["姓名"].."，你武功修为大为长进，近日六大派合议围攻光明顶，你先行一步，探得踪迹速来报。",tid,0)	
							Cls()
							say("是！师傅。",id,1)	
						end
					elseif  r1 == 4 then
						if CC.TX["门派任务"] < 4  then
							say(JY.Person[id]["姓名"].."，金毛狮王谢逊与我武当素有渊源，你速去少林，见机行事。",tid,0)	
							Cls()
							say("是！师傅。",id,1)					
						end
						
					else
						if r1 >= CC.TX["门派任务"] then
							say("已经完成此任务",tid,0)	
