                        end
                        local r1 = LGMsgBox("任务", "我精通伏魔杖法，平日多在寺中精研武功，*下山极少。为师有任务交待你去办。 ", {" 送  信 "," 木人巷 "," 铜人巷 ","南北少林"}, 4, 576,1)
                        if CC.TX["少林初阶"]  == 1 and CC.TX["达摩院"] == 1 and r1 == 1  then
                            if CC.TX["少林任务"] < 1 then
                                say(JY.Person[id]["姓名"].."，为师这里有一封信，你替为师送给Ｒ天台山的智光大师Ｗ。",576,0)	
                                Cls()
                                say("是！师傅。",0,1)
                                instruct_2(208,1)
                                Cls()
                                addevent(100,16,0,3009,3,nil)
                                addevent(100,17,0,3009,3,nil)
                                CC.TX["少林任务"] = 1				
                            end
                        elseif CC.TX["少林中阶"]  == 1 and CC.TX["达摩院"] == 1 and r1 == 2 then
                            if CC.TX["少林任务"] < 2 then
                                say(JY.Person[id]["姓名"].."，你武功修为大为长进，去Ｒ木人巷Ｗ试炼一番。",576,0)	
                                Cls()
                                say("是！师傅。",0,1)
                                CC.TX["少林任务"] = 2

                            end
                        elseif CC.TX["少林高阶"]  == 1 and CC.TX["达摩院"] == 1 and r1 == 3 then
                            if CC.TX["少林任务"] < 3 then
                                say(JY.Person[id]["姓名"].."，你武功修为大为长进，去Ｒ铜人巷Ｗ试炼一番。",576,0)	
                                Cls()
                                say("是！师傅。",0,1)
                                CC.TX["少林任务"] = 3
                                
                            end
                        elseif CC.TX["少林执事"]  == 1 and CC.TX["达摩院"] == 1 and r1 == 4 then
                            if CC.TX["少林任务"] < 4  then
                                say(JY.Person[id]["姓名"].."，本院与南少林寺三年一次的比试到了，这次的比试，你代表为师去向南少林的Ｒ天镜大师Ｗ讨教武艺。",576,0)	
                                Cls()
