	--换秘籍
	newkdef[3008]=function()
		local function wupingisgongType(wuid,typeId)
			local ggid = JY.Thing[wuid]["练出武功"]
			if ggid < 0 then
				return false
			end
			return JY.Wugong[ggid]["武功类型"] == typeId
		end
		say(JY.Person[0]["外号2"].."，如果你能拿到一定数量的Ｒ秘籍残章Ｗ，可以来找我换取一些武功秘籍。",1063,1,"李青萝")
		--ceshi 物品表
		local k = JY.Thing;
		local ceshi = {}
		local gongtype = LGMsgBox("还施水阁", "换取武功秘籍需要花费的残章数量是：   *初阶10个 中阶20个 高阶75个 绝学100个 杂类50~150个*一经换取不退不换。", {"拳法","指法","剑法","刀法","奇门","内功","轻功","杂类"}, 8, nil,1,'李青萝',906)
		if gongtype < 0 then
			return
		end
		for i = 0, JY.ThingNum - 1 do
			if i < 390  or  i == 422  or i == 423 or i == 428 or i == 452 or i ==463 or i == 474 or i ==483 or i == 492 or i == 499 
			or i == 504 or i == 507 or i == 508 or i == 514 or i == 515 or i == 525 or i == 526 or i == 527 then
				local kfname = k[i]["名称"]
				if i == 330 then
					k[i]["名称"] = "追魂夺命剑"
				end
				if i == 350 then
					k[i]["名称"] = "玉女素心剑"
				end
				if string.len(kfname) == 8 then
					kfname = kfname.."  "
				elseif string.len(kfname) == 6 then
					kfname = kfname.."    "
				elseif string.len(kfname) == 4 then
					kfname = kfname.."      "
				end	
				ceshi[i] = {kfname,nil,2}
				if k[i]["类型"] == 2 or i == 182  then
					ceshi[i][3] = 3
					if i == 182 then 
						ceshi[i][3] = 1
					end
					for j = 1,#CC.JLWP do
						if i == CC.JLWP[j] then
							ceshi[i][3] = 1
						end
					end
					if wupingisgongType(i,gongtype)~=true and gongtype ~= 8 then
						ceshi[i][3] = 2
					end
					if  JY.Thing[i]["练出武功"] > 0 and gongtype == 8 then
						ceshi[i][3] = 2
					end
					if JY.Thing[i]["练出武功"] == 379 or JY.Thing[i]["练出武功"] == 179 then
						ceshi[i][3] = 2
					end
				end	
			end	
		end
		local nexty = CC.ScreenH/2-CC.DefaultFont*4 + CC.SingleLineHeight
		local r = 0

		r = ShowMenu2(ceshi, #ceshi, 4, 9, CC.ScreenW/2-CC.DefaultFont*10-20, nexty, 0, 0, 1, 1, CC.DefaultFont, C_ORANGE, C_WHITE,"灰色是暂未获得秘籍")
		if r == 0 then
			return
		end
		local wg = JY.Thing[r]["练出武功"]
		if JY.Thing[r]["练出武功"] > 0 then
			if JY.Wugong[JY.Thing[r]["练出武功"]]["层级"] <= 2 and (CC.MJCZ - JY.Wugong[JY.Thing[r]["练出武功"]]["层级"]*10) >= 0 then
				instruct_2(r,1)
				instruct_2(550,-JY.Wugong[JY.Thing[r]["练出武功"]]["层级"]*10)
			elseif JY.Wugong[JY.Thing[r]["练出武功"]]["层级"] > 2 and (CC.MJCZ - JY.Wugong[JY.Thing[r]["练出武功"]]["层级"]*25) >= 0 then
				instruct_2(r,1)
				instruct_2(550,-JY.Wugong[JY.Thing[r]["练出武功"]]["层级"]*25)
			else
				say(JY.Person[0]["外号2"].."，秘籍残章数量不够哦。",1063,1,"李青萝")
			end
		else
			if r == 182 then
				if (CC.MJCZ - 200) >= 0 then	
					instruct_2(r,1)
					instruct_2(550,-200)	
				else
					say(JY.Person[0]["外号2"].."，秘籍残章数量不够哦。",1063,1,"李青萝")
				end
			else
				if (CC.MJCZ - 100) >= 0 then 
					if r ~= 235 or r~=372 then 	
						instruct_2(r,1)
						instruct_2(550,-50)
					else
						instruct_2(r,1)
						instruct_2(550,-150)
					end
				else
					say(JY.Person[0]["外号2"].."，秘籍残章数量不够哦。",1063,1,"李青萝")
				end
			end	
		end
		return
	end
