			CC.Month = CC.Month + 5
			light()
		end	
		if r == 5 then 
			dark()
			CC.Year = CC.Year + 1
			light()
		end
		if r == 6 then 
			dark()
			CC.Year = CC.Year + 5
			light()
		end
