						tfme[#tfme+1] = i
					end
				end
			end
			if CC.PTF[0] ~= nil then 
				for i,v in pairs(CC.PTF[0]) do 
					if v == 1 then 
						tfme[#tfme+1] = i
					end
				end
			end
			local menu = {}
