	--北京比武
	newkdef[3043] = function()
		instruct_25(27,25,35,15)
		say("汤大侠这功夫帅极了！在下再练二十年也练不成。可是天外有天，人上有人，在真正武学高看来，那也平平无奇。", 748, 0) --凤天南
		Cls()
		say("凤老师说得半点也不错，在武学高瞧来，真是一钱也不值。不过只要能胜得过凤老师，我也心满意足了。", 731, 0) --汤沛
		Cls()
		say("你们两位尽斗什么口？天也快亮啦，九只玉龙杯，六只已有了主儿。咱们今晚定了玉龙杯的名分，明晚再来争Ｒ冷月宝刀和闯王军刀Ｗ。还有哪一位英雄，要上来跟凤老师比划？", 732, 0) --赛尚鄂
		Cls()
		say("苗大侠所言非虚，正在此处。", 0, 1)
		Cls()
		instruct_25(35,15,27,25)
		say("Ｄ＜点点头＞Ｗ，这凤天南我正寻他得紧，我去会会他。", 1, 1)
		Cls()
		instruct_30(27,25,27,22)
		instruct_30(27,22,33,22)
		instruct_30(33,22,33,18)
		say("Ｄ＜朗声叫道＞Ｗ，且慢，我来斗一斗凤天南。", 1, 1)
		Cls()
		say("九家半掌门到", 400, 1)
		Cls()
		dark()
		addevent(-2,33,1,nil,1,4932*2)
		light()
		say("Ｄ＜胡斐心头一凛，抬头向厅门看去，登时惊得呆了。只见门进来一个妙龄尼姑，缁衣芒鞋，执云帚，正是袁紫衣。只是她头上已无一根青丝，脑门处并有戒印。胡斐双眼一花，还怕是看错了人，迎上一步，看得清清楚楚，却不是袁紫衣是谁？＞Ｗ", 1, 1)
		Cls()
		say("Ｄ＜霎时间胡斐只觉天旋地转，心乱成一片＞Ｗ你...你是袁...", 1, 1)
		Cls()
		say("Ｄ＜双合十，黯然道＞Ｗ小尼圆性。我一直瞒着你，是我不好。我自幼出家，法名叫做‘圆性’。我说‘姓袁’，一则是我娘的姓，二则便是将‘圆性’两字颠倒过来。‘紫衣’，那便是缁衣芒鞋的‘缁衣’！", 587, 1)
		Cls()
		say("Ｄ＜怔怔的望着她，欲待不信此事，但眼前的袁紫衣明明是个妙尼，隔了半晌＞Ｗ，你……你为什么要骗我？。", 1, 1)
		Cls()
		say("Ｄ＜低垂了头，双眼瞧着地下，轻轻地道＞Ｗ我奉师父之命，从回疆到原来，单身一人，若作僧尼之装，长途投宿打尖甚是不便，因此改作俗家打扮。我头上装的是假发，饮食不沾荤腥，想是你没瞧出来。", 587, 1)
		Cls()
		--instruct_30(33,18,29,13)
		say("还有哪一位来跟五虎门凤老师比试？。回大帅：这只玉龙御杯，便赏给这位老师？", 732, 1)
		Cls()	
		say("很好，很好！", 720, 1,'福康安')
		Cls()
		say("石老师，贵门派和大名怎么称呼？", 732, 1)
		Cls()
		say("草字万嗔，至于门派嘛，就叫作药王门吧。", 719, 1,'石万嗔')
		Cls()
		say("药王门掌门人石万嗔，五虎门掌门人凤天南收执。恭喜，恭喜！Ｄ＜将托盘递了过去。大智禅师等人每人伸取了一只玉龙杯。突然之间，个人上犹似碰到了烧得通红的烙铁，实在拿捏不住，一齐松。乒乒乓乓一阵清脆的响声过去，只玉杯同时在青砖地上砸得粉碎。＞", 732, 1)
		Cls()
		dark()
		light()
		--instruct_30(29,13,33,18)
		say("Ｄ＜云帚指着汤沛，朗声说＞Ｗ汤沛，这是皇上御赐的玉杯，你如此胆大妄为，竟敢暗施诡计，尽数砸碎。你心存不轨，和红花会暗勾结，要拆散福大帅的天下掌门人大会。你这般大逆不道，目无长上，天下英雄都容你不得！", 587, 1)
		Cls()
		say("小妖尼，这种事也能空口白赖、胡说八道么？", 731, 1)
		Cls()
		say("八卦门的掌门人王老师、鹰爪雁行门的掌门人周老师，你们都认得我是谁。这九家半的总掌门我是不当的了。可是我是胡说八道之人呢，还是有担当有身分之人？你们两位且说一句。如若有假，这九家半的总掌门我是不当的了。", 587, 1)
		Cls()
		say("您老人家武艺超群，在下甚是敬服，为人又宽宏大量，实是当世武林的杰出人材。在下相信您老人家言而有信，顾全大体，尊重武林同道的颜面，若非万不得已，决不揭露汤大侠的奸计。", 741, 1)
		Cls()
		say("拿下了！", 720, 1,'福康安')
		Cls()
		if WarMain(108) == false then 
			instruct_15()
			Cls()  --清屏
			do return end
		end
		say("你……你是银姑，银姑的！", 731, 1)
		Cls()
		say("你终于认出来了。", 587, 1)
		Cls()
		say("福大帅，这尼姑是小人的仇家。她设下圈套，陷害于我。大帅，你千万信她不得。", 731, 1)
		Cls()
		say("不错，我是你的仇家。我母亲走投无路，来到你家。你这人面兽心的汤大侠，见我母亲美貌，竟使暴力侵犯于她，害得我母亲悬梁自尽。这事可是有的？", 587, 1)
		Cls()
		say("不错，确有此事。", 731, 1)
		Cls()
		say("我一直想杀了你这禽兽，替亡母报仇，可是你武功太强，我斗你不过，只有日夜在你屋顶窗下窥伺。嘿嘿，天假其便，给我听到你跟红花会赵半山、常氏兄弟、石双英这些匪首阴谋私议。适才抢夺玉龙杯的那个少年书生，便是红花会总舵主陈家洛的书僮心砚，是也不是？", 587, 1)
		Cls()
		say("此人正是心砚。他好大的胆子，竟不怕我认他出来！", 720, 1,'福康安')
		Cls()
		say("我怎认得他？倘若我跟红花会勾结，何以又出擒住他？", 731, 1)
		Cls()
		say("你脚做得如此干净利落，要是我事先没听到你们暗的密议，也决计想不到这阴谋。我问你，你汤大侠的点穴法另具一功，你下点了人家穴道之后，本来旁人再也无法解得开。可是适才你点了那红花会匪徒的穴道，何以大厅上灯火齐熄？那匪徒身上的穴道又何以忽然解了，得以逃去？", 587, 1)
		Cls()
		say("Ｄ＜张口结舌＞Ｗ这个……这个……想是暗有人解救。", 731, 1)
		Cls()
		say("暗解救之人，除了汤沛汤大侠，天下再无第二个。当时除你之外，还有谁站在那人的身边？福大帅，这汤沛和红花会匪徒计议定当，假装将那匪徒心砚擒获，放在你身旁，再由另一批匪徒打灭烛火，那心砚便乘乱就近向你行刺。这批匪徒意料之，众卫士见那书生已被点了穴道，动弹不得，自不会防他行刺。天幸福大帅洪福齐天，逢凶化吉。众卫士又忠心耿耿，防卫周密，烛火灭熄之后，立即一齐挡在大帅身前保护，贼人的奸计才不得逞。", 587, 1)
		Cls()
		say("你胡说八道，哪有此事？", 731, 1)
		Cls()
		say("王大人，周大人，适才贼人的奸计是否如此？", 587, 1)
		Cls()
		say("那书生确是曾扑到大帅身前来，幸好未能。黑暗之中的确有人过来，功夫厉害得很，我们只好拚了命抵挡，却没想到竟是汤沛，当真凶险得紧。", 741, 1)
		Cls()
		say("你……你满口胡言！适才你又不在厅上，如何得知？凤天南，原来是你从捣鬼！你要我暗助你，令你五虎门在掌门人大会压倒群雄，这时却又叫你女儿来陷害于我。", 731, 1)
		Cls()
		say("Ｄ＜张口结舌＞Ｗ我女儿？她……她是我女儿？", 748, 1)
		Cls()
		say("你还在这里假痴假呆，装作不知。你瞧瞧这小尼姑，跟当年的银姑有什么分别？好，事已如此，我也不必隐瞒。那无影银针，是你放的还是我放的？你若能放，那便射我一枚试试。", 731, 1)
		Cls()
		say("Ｄ＜见袁紫衣不敢尝试，知是前面受了此人的言语挑拨，怒火中烧喝道＞Ｗ拿下这尼姑。", 720, 1,'福康安')
		Cls()
		if WarMain(109) == false then 
			instruct_15()
			Cls()  --清屏
			do return end
		end
		dark()
		null(-2,33)
		null(-2,22)
		null(-2,23)
		addevent(-2,11,1,nil,1,4932*2)
		light()
		instruct_19(20,44)
		instruct_40(3)
		say("胡大哥，这把两把宝刀我看着不凡，顺手拿了来，送给你了，你不要再生我的生气了。程姑娘人很好，你要好好待她。你以后别再想着我，我也永远不会再记到你。", 587, 1)
		Cls()
		instruct_2(202,1)
		instruct_2(45,1)
		if CC.TX['程灵素'] == 1 then 
			say("不，我永远永远要记着你，记着你。你……你到哪里去？", 1, 1)
			Cls()
			say("你何必管我？此后便如一年之前，你不知世上有我，我不知世上有你，岂不干净？", 587, 1)
			Cls()
			dark()
			null(-2,11)
			light()
			say("早先我有个故人让我得空去一趟Ｒ玉笔山庄Ｗ，说是有关我生母的事相告。", 1, 1)
			Cls()
			say("我倍你跑一趟。", 0, 1)
			Cls()
			CC.TX['雪'] = 1
			CC.TX['飞'] = 36
			do return end 
		end
		say("不，我永远永远要记着你，记着你。你……你到哪里去？", 1, 1)
		Cls()
		say("我……我也不知道去哪里……我师傅不要我了。", 587, 1)
		Cls()
		say("那……不如我们结伴去Ｒ玉笔山庄Ｗ，日后再作打算。", 1, 1)
		Cls()
		say("也行。", 587, 1)
		Cls()
		if instruct_20() then  --判断队伍是否已满
			Cls()  --清屏
			TalkEx("你的队伍已满，我就直接去小村吧。", 587, 0)  --对话
			Cls()  --清屏
			null(-2,11)
			instruct_3(70, 124,1,0,10007,0,0,4957*2,4957*2,4957*2,-2,-2,-2)  --修改场景事件
			Cls()  --清屏
			do return end  --无条件结束事件
		end
		instruct_10(587)  --加入队伍
		null(-2,11)
		CC.TX['雪'] = 1
		CC.TX['飞'] = 36
		do return end
