	--畅想少林入门
	newkdef[3044]=function()
		if JY.Person[0]['门派'] == 0 then 
			local mpsf = 149
			if instruct_4(234)  then
				say("这是故人的信件，你想拜入我"..MP_name(mpsf).."吗？",mpsf,0)
				if DrawStrBoxYesNo(-1, -1, "是否拜入"..MP_name(mpsf).."，成为"..MP_name(mpsf).."弟子?", C_WHITE, CC.DefaultFont) then
					if JY.Person[0]['性别'] == 0 then 
						instruct_2(234,-1)
						JY.Person[0]['门派'] = JY.Person[mpsf]['门派']
						DrawStrBoxWaitKey(JY.Person[0]['姓名'].."成为"..MP_name(mpsf).."弟子", C_GOLD, CC.DefaultFont,nil, C_RED)
						local menu ={"般若堂","达摩院"}
						local R = LGMsgBox("选择", " 你要入哪个院堂学习武功？", menu, #menu, 149)
						if R == 1 then
							CC.TX['般若堂'] = 1
						elseif R == 2 then 
							CC.TX['达摩院'] = 1
						else
							CC.TX['般若堂'] = 1
						end
						CC.TX["少林初阶"]  = 1
					else
						say("本寺只收男弟子，施主请自便。"..MP_name(mpsf).."吗？",mpsf,0)
					end
				end
			end
		end
	end

